mapper.update:
UPDATE INTEREST 
SET REGION_CODE = SELECT SUBSTR(REGION_CODE,0,5) FROM REGION WHERE LEGAL_DONG LIKE '%'||#{city} || ' ' ||#{state}||'%' AND ROWNUM=1;}
ESTATE_TYPE = #{estateType, typeHandler=strArrTypeHandler}, PET = #{pet}, ELEVATOR = #{elevator},
PARKING = #{parking}, SUBWAY = #{subway}, CITY = #{city}, STATE = #{state} 
WHERE MEMBER_NO = #{memberNo}

UPDATE TBL_CART
SET CART_CHECK = CART_CHECK + 1
WHERE MEMBER_NO = #{memberNo} AND ESTATE_NO = #{estateNo}

UPDATE TBL_CART
SET CART_CHECK = 0
WHERE MEMBER_NO = #{memberNo} AND ESTATE_NO = #{estateNo}
